import React, { Component } from 'react'
import { api } from '../../services/ApiConfig'


export default class Movies extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: []
        }
    }
    componentDidMount() {
        this.fetchMovies()
    }

    fetchMovies = async () => {
        try {
            const movies = await api.get('/allMovies')
            this.setState({
                movies: movies.data
            })
        } catch (error) {
            console.error(error)
        }
    }

    renderMovies = () => {
        if (this.state.movies.length) {
            return this.state.movies.map((movies))

        }
    }
}
