import React from 'react'
const url = 'https://image.tmdb.org/t/p/w500/'
const placeholder = 'https://res.cloudinary.com/teepublic/image/private/s--wDf38vff--/t_Preview/b_rgb:000000,c_limit,f_jpg,h_630,q_90,w_630/v1562169720/production/designs/5227093_0.jpg'

export const CardHeader = ({ imageUrl }) => {
	const image = imageUrl === '' ? placeholder : imageUrl
	return (
		<div className='card-header'>
			<img className="movieImage" src={image.includes('https') ? image : url + image} alt='card header' />
		</div>
	)
}
