import React, { Component } from 'react'
import { api } from './Services/ApiConfig'
import { MovieCard } from './Shared/MovieCard.js'

export default class Movies extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: []
        }
    }
    componentDidMount() {
        this.fetchMovies()
    }

    fetchMovies = async () => {
        try {
            const movies = await api.get('/allMovies')
            this.setState({
                movies: movies.data
            })
        } catch (error) {
            console.error(error)
        }
    }

    renderMovies = () => {
        this.state.movies.map((movie) => {
            console.log('movie', movie.Title)
            return (
                <MovieCard key={movie.id} movie={movie} />
            )
        })
    }

    render() {
        console.log('state movies', this.state.movies)
        return (
            <div>
                {this.renderMovies()}
            </div>
        )
    }
}
