import React, { Component } from 'react'
import MovieForm from '../MovieForm'
import {api} from '../../Services/ApiConfig'


export default class CreateMovie extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            description: '',
            image_url: '',
            genre: '',
            revenue: '',
            budget: '',
            date: '',
            companies: '',

        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const {name, description, image_url, genre, revenue, budget, date, companies} = this.state
        const data= {
            name,
            description,
            image_url,
            genre,
            revenue,
            budget,
            date,
            companies
        }
        api.post('/allMovies', data)
    }
    handleChange = (e) => this.setState({
        [e.target.name]: e.target.value
    })

    render() {
        const { name, description, image_url, genre, revenue, budget, date, companies } = props
        return (
            <div className="movie-forms">
                <h3>Add Movie Info!</h3>
                <MovieForm
                    formData={{ name, description, image_url, genre, revenue, budget, date, companies}}
                    onChange={this.handleChange}
                    onSubmit={this.handleSubmit}
                />
                {this.state.errorMsg ? (
                    <p className="error-text">{this.state.errorMsg}</p>
                ): null}
            </div>
        )
    }

}
