import React, { Component } from 'react'
import { api } from './Services/ApiConfig2'
import WidgetCard from ''





export default class Widget extends Component {
    constructor (props) {
        super(props)
        this.state = {
            movies: []
        }
    }

    componentDidMount() {
        this.fetchWidgetMovie()
    }

    fetchWidgetMovie = async () => {
        try {
            const widgetMovie = await api.get()
            this.setState ({
                movies: widgetMovie.data
            })
        } catch (error) {
            console.error(error)
        }
    }

    render() {
        return  (
            this.state.movies.map(movie => {
                return (
                    <WidgetCard movie={movie}/>
                )
            })
        )
    }
}
