import React, { Component } from 'react'
import { api } from './Services/ApiConfig'
import { MovieCard } from './Shared/MovieCard.js'

export default class Movies extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: [],
            movieToDelete: {},
            toDelete: false,
        }
    }
    componentDidMount() {
        this.fetchMovies()
    }

    fetchMovies = async () => {
        try {
            const movies = await api.get('/allMovies')
            this.setState({
                movies: movies.data
            })
        } catch (error) {
            console.error(error)
        }
    }

    handleDeleteMovies = () => {
        api.delete(`./allMovies${this.state.moviesToDelete.id}`).then(() => {
            const allMovies = this.state.movies
            allMovies.splice(this.state.moviesToDelete.index, 1);
            this.setState({movies: allMovies})
        }).then(()=> this.handleCloseMovie())
        .catch((err) => console.error(err))
    }

    handleOpenModal = (movie, index) =>
    this.setState({ toDelete: true, movies})

    render() {
        return (
            this.state.movies.map(movie => {
                return (
                    <MovieCard key={movie.id} movie={movie} />
                )
            })
        )
    }
}
