import React, { Component } from 'react'
import  { api }  from './Services/ApiConfig'
import { MovieCard } from './Shared/MovieCard.js'
import { CardHeader } from './Shared/CardHeader.js'
import { CardBody } from './Shared/CardBody.js'  
import  { Button }  from './Shared/Button.js'


export default class Movies extends Component {
    constructor(props) {
        super(props)
        this.state = {
            movies: []
        }
    }
    componentDidMount() {
        this.fetchMovies()
    }

    fetchMovies = async () => {
        try {
            const movies = await api.get('/allMovies')
            this.setState({
                movies: movies.data
            })
        } catch (error) {
            console.error(error)
        }
    }

    render ()  {
        if (this.state.movies.length) {
            return this.state.movies.map((movie) => (
                <MovieCard key={movie.id}>
                    <CardHeader imageUrl={movie.image_url}/>
                    <CardBody>
                        <h3>{movie.Title}</h3>
                        <p>{movie.runtime}</p>
                        <p>{movie.Overview}</p>
                    </CardBody>
                    <Button title='Read More'/>
                </MovieCard>
            ))

        }
    }
}
